import {useState} from "react";

const Personnel = () => { 
  const [name, setName] = useState('');
  const [department, setDepartment]= useState('');
  const [officeHours, setOfficeHours] = useState('');
  const [officeNumber, setOfficeNumber] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [isPending, setIsPendign] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    const personnel = {name, department, officeHours, officeNumber, phone, email};

    setIsPendign(true);

    fetch('http://localhost:4000/admin/personnel', {
      method: 'POST',
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify(personnel)
    })
      .then(() => {
        console.log("new personnel added")
        setIsPendign(false);
      })
      .catch((e) => {
        console.log(JSON.stringify(personnel));
        console.log("Couldn't add personnel" + e.message);
      })
  }

  return (
    <div className="personnel">
      <h1>Add and update Personnel</h1>
        <form onSubmit={handleSubmit}>
          <label>Personnel name:</label>
            <input
                type="text"
                required
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
          <label>Personnel department:</label>
            <input
                type="text"
                required
                value={name}
                onChange={(e) => setDepartment(e.target.value)}
            />
          <label>Personnel OfficeHours:</label>
            <input
                type="text"
                required
                value={officeHours}
                onChange={(e) => setOfficeHours(e.target.value)}
            />
          <label>Personnel OfficeNumber:</label>
            <input
                type="text"
                required
                value={officeNumber}
                onChange={(e) => setOfficeNumber(e.target.value)}
            />
          <label>Personnel Phone number:</label>
            <input
                type="text"
                required
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
            />
          <label>Personnel email:</label>
            <input
                type="text"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
            />
        </form>
    </div>
  );
}

export default Personnel;
